<?php require_once __DIR__.'/../includes/config.php'; require_once __DIR__.'/../includes/functions.php';
session_start(); if(!is_admin($pdo)) header('Location: login.php');
$txs = $pdo->query('SELECT t.*,u1.email as from_email,u2.email as to_email FROM transactions t LEFT JOIN users u1 ON t.sender_id=u1.id LEFT JOIN users u2 ON t.receiver_id=u2.id ORDER BY t.created_at DESC')->fetchAll();
?><!doctype html><html><head><meta charset='utf-8'><title>Transactions</title><link rel='stylesheet' href='../assets/css/style.css'></head><body>
<div class='topbar'><a href='index.php'>Admin</a></div><div class='container'><h2>All Transactions</h2><table><tr><th>Date</th><th>From</th><th>To</th><th>Amount</th><th>Status</th></tr><?php foreach($txs as $t){ echo '<tr><td>'.$t['created_at'].'</td><td>'.htmlspecialchars($t['from_email']).'</td><td>'.htmlspecialchars($t['to_email']).'</td><td>$'.number_format($t['amount'],2).'</td><td>'.htmlspecialchars($t['status']).'</td></tr>'; } ?></table></div></body></html>
